import React from 'react';
import logo from './logo.svg';
import NewItemForm from './NewItemForm.js';
import './App.css';

class App extends React.Component {
	constructor(props) {
		super(props)
		this.state = {
			items: [
				{
					title: "Lemons",
					count: 10,
					purchased: false,
				},
				{
					title: "Avocados",
					count: 3,
					purchased: false,
				}
			]
		}

		this.addGroceryItem = this.addGroceryItem.bind(this);
		this.checkItem = this.checkItem.bind(this);
	}

	addGroceryItem(groceryItem) {
		let items = this.state.items.slice();
		items.push(groceryItem);

		this.setState({
			items: items
		});
	}

	render() {
		return (
			<section className="section">
				<div className="container is-fluid">
					<div className="columns">
						<div className="column is-half is-offset-one-quarter">

							<h1 className="title">Grocery List</h1>

							<NewItemForm addItem={this.addGroceryItem} />

							<div className="my-5">
								<ul>
									{this.props.itemList.map((item, index) => (
										<li key={index}>
											<label
												className="checkbox"
												style={{ textDecoration: item.purchased ? "line-through" : "none", }} >
												<input checked={item.purchased} className="mr-3" onClick={this.checkGroceryItem(index)} type="checkbox" />
												{item.title} <span className="has-text-weight-light">({item.count})</span>
											</label>
										</li>
									))}
								</ul>
							</div>

						</div>
					</div>
				</div>
			</section>
		);
	}
}

export default App;
