import React from 'react';
import logo from './logo.svg';
import './App.css';
import NewNoteForm from './NewNoteForm.js';
import Note from './Note.js';

class App extends React.Component {
	constructor(props) {
		super(props)
		this.state = {
			notes: []
		}

		this.addNote = this.addNote.bind(this);
		this.getNotes = this.getNotes.bind(this);
		this.writeNotes = this.writeNotes.bind(this);
	}

	addNote(newNote) {
		let notes = this.state.notes.slice();
		notes.push(newNote);

		this.setState({
			notes: notes
		})
	}

	getNotes() {
		let file = "./notesList.txt";
		let reader = new FileReader();
		reader

		let formattedArray = [];
		for (const i in formattedArray) {
			let noteArray = fileArray[i].split("\n");
			for (const x in noteArray) {
				let obj = {
					noteTitle: noteArray[0],
					noteDescription: noteArray[1],
					date: noteArray[2]
				}
				formattedArray.push(obj);
			}
		}

		this.setState({
			notes: formattedArray
		})
	}

	writeNotes() {

	}

	render() {
		return (
			<section className="section">
				<div className="container is-fluid">
					<div className="columns">
						<div className="column is-half is-offset-one-quarter">

							<h1 className="title" style={{ color: "white" }}>Rocket League Notes</h1>

							<NewNoteForm addNote={this.addNote} getNotes={this.getNotes} />

							<div className="my-2">
								<ul style={{ listStyle: "disc outside", paddingLeft: "20px"}}>
									{this.state.notes.map((note, index) => (
										<Note note={note} />
									))}
								</ul>
							</div>

						</div>
					</div>
				</div>
			</section>
		);
	}
}

export default App;
