import React from 'react';

class DisplayGroceryItems extends React.Component {
    constructor(props) {
        super(props)
        this.state = {}

        this.checkGroceryItem = this.checkGroceryItem.bind(this);
    }

    checkGroceryItem(item, index) {
        let items = this.props.itemList.slice();

        items[index].purchased = !items[index];

        this.props.checkItem(items);
    }

    render() {
        return(
            <div className="my-5">
              <ul>
                {this.props.itemList.map((item, index) => (
                  <li key={index}>
                    <label
                      className="checkbox"
                      style={{ textDecoration: item.purchased? "line-through" : "none", }} >
                      <input checked={item.purchased} className="mr-3" onClick={this.checkGroceryItem} type="checkbox" />
                      {item.title} <span className="has-text-weight-light">({item.count})</span>
                    </label>
                  </li>
                ))}
              </ul>
            </div>
        )
    }
}

export default DisplayGroceryItems
