import React from 'react';
import './App.css';
import NewNoteForm from './NewNoteForm.js';
import Note from './Note.js';

class App extends React.Component {
	constructor(props) {
		super(props)
		this.state = {
			notes: [],
			import: false
		}

		this.addNote = this.addNote.bind(this);
		this.getNotes = this.getNotes.bind(this);
	}

	addNote(newNote) {
		let notes = this.state.notes.slice();
		notes.push(newNote);

		this.setState({
			notes: notes
		})
	}

	getNotes() {
		if (document.getElementById("note-file").files[0]) {
			let file = document.getElementById("note-file").files[0];
			let reader = new FileReader();
			reader.readAsText(file);

			let fileArray;
			let formattedArray = [];

			reader.onload = () => {
				fileArray = reader.result.toString().split("\r\n\r\n");
				console.log(fileArray)

				for (const i in fileArray) {
					let noteArray = fileArray[i].split("\r\n");

					let obj = {
						noteTitle: noteArray[1],
						noteDescription: noteArray[2],
						date: noteArray[0]
					}

					formattedArray.push(obj);

					this.setState({
						notes: formattedArray
					})
				}
			}
		}
	}

	render() {
		return (
			<section className="section">
				<div className="container is-fluid">
					<div className="columns">
						<div className="column is-half is-offset-one-quarter">

							<h1 className="title" style={{ color: "white" }}>Rocket League Notes</h1>

							<NewNoteForm addNote={this.addNote} getNotes={this.getNotes} />

							<div class="file my-3">
								<label class="file-label">
									<input id="note-file" class="file-input" type="file" name="resume" />
									<span class="file-cta">
									<span class="file-icon">
										<i class="fas fa-upload"></i>
									</span>
									<span class="file-label">
										Choose a file…
									</span>
									</span>
								</label>
								<label class="file-label ml-3">
									<button class="button" onClick={this.downloadNotes}>
										<span class="file-icon">
											<i class="fas fa-download"></i>
										</span>
										Download Notes	
									</button>
								</label>
							</div>

							<div className="my-2">
								<ul style={{ listStyle: "disc outside", paddingLeft: "20px"}}>
									{this.state.notes.map((note, index) => (
										<Note note={note} />
									))}
								</ul>
							</div>

						</div>
					</div>
				</div>
			</section>
		);
	}
}

export default App;
