import React from 'react';

class NewItemForm extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			itemCount: 1,
			itemTitle: "",
		};

		this.submitForm = this.submitForm.bind(this);
		this.updateItemCount = this.updateItemCount.bind(this);
		this.updateItemTitle = this.updateItemTitle.bind(this);
	}

	updateItemCount(event) {
		this.setState({
			itemCount: event.target.value
		});
	}

	updateItemTitle(event) {
		this.setState({
			itemTitle: event.target.value
		});
	}

	submitForm(event) {
		event.preventDefault();

		let item = {
			title: this.state.itemTitle,
			count: this.state.itemCount,
			purchased: false,
		};

		this.props.addItem(item);

		this.setState({
			itemCount: 1,
			itemTitle: ""
		});
	}

	render() {
		return (
			<form onSubmit={this.submitForm}>
				<div className="field has-addons">
					<div className="control">
						<input
							style={{ maxWidth: "6em" }}
							className="input"
							value={this.state.itemCount}
							onChange={this.updateItemCount}
							type="number"
							placeholder="2"
							min="1"
							aria-label="number to purchase" />
					</div>

					<div className="control is-expanded">
						<input
							className="input"
							type="text"
							value={this.state.itemTitle}
							onChange={this.updateItemTitle}
							placeholder="Item to buy"
							aria-label="item to purchase" />
					</div>

					<div className="control">
						<input
							className="button is info"
							type="submit"
							value="Add item" />
					</div>

				</div>
			</form>
		)
	}
}

export default NewItemForm
