import React from 'react';

class DisplayDescription extends React.Component {
    render() {
        if (this.props.note.noteDescription == "-") {
            return(
                <>
                   <ul style={{ listStyle: "disc outside", paddingLeft: "20px"}}>
                        <li class="my-3">
                            <label style={{ color: "white" }}>
                                <span style={{ fontWeight: "bold" }}>{this.props.note.date} -</span> {this.props.note.noteTitle}
                            </label>
                        </li>
                    </ul>
                    <hr style={{
                        borderTop: "1px solid grey"
                    }}></hr>
                </>
            )
        } else {
            return(
                <>  <ul style={{ listStyle: "disc outside", paddingLeft: "20px"}}>
                        <li class="my-3">
                            <label style={{ color: "white" }}>
                                <span style={{ fontWeight: "bold" }}>{this.props.note.date} -</span> {this.props.note.noteTitle}
                            </label>
                        </li>
                    </ul>
                    <textarea
                        className="textarea"
                        value={this.props.note.noteDescription}
                        style={{
                            backgroundColor: "#1a1c20",
                            borderColor: "grey",
                            color: "white"
                        }}
                        aria-label="note description"
                        disabled
                    />
                </>
            )   
        }
    }

    
}

export default DisplayDescription
